import React from 'react'

const Countries = (props) => {

    if(props.countriesToShow.length > 10){

        return (
            <div>
                <p>Too many matches, specify another filter</p>
            </div>
        )
    }

    return(

        props.countriesToShow.map(countr => 
            <p>{countr.name}</p>)      

    )
}

export default Countries
